getQuestions = function(){
  questions = NULL
  ib = fromJSON(item_bank)
  print(ib)
  if(ib$table_type == "custom") {
    test_id = concerto$mainTest$id
    table = ib$custom_table
    question_column = ib$custom_question_column$column
    response_options_column = ib$custom_response_options_column$column
    trait_column = ib$custom_trait_column$column
    order_column = ib$custom_order_column$column
    test_id_column = ib$custom_test_id_column$column

    questions = concerto.table.query("SELECT id, `{{question_column}}` AS `question`, `{{response_options_column}}` AS `response_options`, `{{trait_column}}` AS `trait` FROM `{{table}}` WHERE `{{test_id_column}}` = {{test_id}} ORDER BY `{{order_column}}` ASC", list(
      question_column=question_column,
      response_options_column=response_options_column,
      trait_column=trait_column,
      order_column=order_column,
      table=table,
      test_id_column=test_id_column,
      test_id=test_id
    ))
  }
  if(ib$table_type == "direct") {
    if(length(ib$direct_table) > 0) {
      for(i in 1:length(ib$direct_table)) {
        ib$direct_table[[i]]$response_options = toJSON(ib$direct_table[[i]]$response_options) #response options don't fit into flat table, so turn them back to JSON.
        print("ib$direct_table[[i]] = ")
        print(ib$direct_table[[i]])
        questions = rbind(questions, data.frame(ib$direct_table[[i]], stringsAsFactors=F))
      }
    }
  }

  if(dim(questions)[1] > 0 && order == "random") {
    questions = questions[sample(1:dim(questions)[1]),]
  }

  print(questions)

  return(questions)
}

getContent = function(question, current_item, answers) {
  content = paragraph

  content = paste0(content, "<div class='formField'>")
  content = paste0(content, question$question)
  content = paste0(content, getResponseOptions(question, current_item, answers))
  content = paste0(content, "</div>")

  return(content)
}

getResponseOptions = function(question, current_item, answers) {
  content = ""
  options = NULL
  print("getResponseOptions()")
  print(question)
  o = question$response_options
  if(is.character(o)) { o = fromJSON(o) }

  if(o$response_type == "multiple_choice") {
    #response options
    for(i in 1:length(o$response_options)) {
      options = rbind(options, as.list(o$response_options[[i]]))
    }

    if(randomized_response_options == "1") {
      if(dim(options)[1] > 0) {
        options = options[sample(1:dim(options)[1]),]
      }
    }

    content = "<div class='catResponseGroup'>"
    section_class = "catResponseSectionVertical"
    section_styles = ""
    if(responses_layout == "horizontal") { 
      section_class = "catResponseSectionHorizontal" 
      section_styles = paste0("width: ",response_width)
    }

    for(i in 1:dim(options)[1]) {
      option = options[i,]
      checked = ""
      if(!is.null(answers[current_item]) && !is.na(answers[current_item]) && answers[current_item] == option$value) { checked = "checked" }
      content = paste0(content, "<div class='",section_class,"' style='",section_styles,"'><label for='input_response_",i,"'><table class='catResponseTable'><tr>")
      content = paste0(content, "<td class='catResponseButtonCell'><input type='radio' id='input_response_",i,"' name='response' value='",option$value,"' ",checked," /></td>")
      content = paste0(content, "<td class='catResponseLabelCell'>",option$label,"</td>")
      content = paste0(content, "</tr></table></label></div>")
    }
    content = paste0(content, "<div style='clear:both;'></div>")
    content = paste0(content, "</div>")
  } else if (o$response_type == "open_text") {
    #open question
    value = ""
    if(!is.null(answers[current_item]) && !is.na(answers[current_item])) { value = answers[current_item] }
    content = paste0("<div><input type='text' name='response' style='width:100%;' value='",value,"' /></div>")
  }
  return(content)
}

shouldStop = function(response, answers, questions, current_item, next_item) {
  sr_time = fromJSON(stopping_time)
  if(!is.null(sr_time$type) && sr_time$type == "test" && response$isTimeout == "1") { return(TRUE) }

  sr_count = fromJSON(stopping_item_count)

  if((sr_count$enabled == "1" && current_item >= as.numeric(sr_count$count)) || next_item > dim(questions)[1]) { return(TRUE) }
  return(FALSE)
}

saveResponse = function(response, question, rb, correct, scores, correctness, current_item) {
  if(is.null(response$response)) { return(scores) }

  table = "default_linear_response_table"
  item_id_column = "item_id"
  response_column = "response"
  time_taken_column = "time_taken"
  session_internal_id_column = "session_internal_id"
  correct_column = "correct"
  trait_column = "trait"
  if(rb$table_type == "custom") {
    table = rb$custom_table
    item_id_column = rb$custom_item_id_column$column
    response_column = rb$custom_response_column$column
    time_taken_column = rb$custom_time_taken_column$column
    session_internal_id_column = rb$custom_session_internal_id_column$column
    correct_column = rb$custom_correct_column$column
    trait_column = rb$custom_trait_column$column
  }
  session_internal_id = 0
  if(!is.null(session) && is.list(session)) {
    session_internal_id = session$internal_id
  } else {
    session_internal_id = concerto$session$id
  }

  r = concerto.table.query("SELECT `id` FROM `{{table}}` WHERE `{{item_id_column}}`={{item_id}} AND `{{session_internal_id_column}}`={{session_internal_id}}", list(
    table=table,
    session_internal_id_column=session_internal_id_column,
    session_internal_id=session_internal_id,
    item_id_column=item_id_column,
    item_id=question$id
  ))
  if(dim(r)[1] > 0) { 
    rid = r[1,"id"]
    concerto.table.query("UPDATE `{{table}}` SET `{{response_column}}`='{{response}}', `{{time_taken_column}}`={{time_taken}}, `{{correct_column}}`={{correct}} WHERE `id`={{id}}", list(
      table=table,
      item_id_column=item_id_column,
      item_id=question$id,
      response_column=response_column,
      response=response$response,
      time_taken_column=time_taken_column,
      time_taken=response$timeTaken,
      correct_column=correct_column,
      correct=correct,
      trait_column=trait_column,
      trait=question$trait,
      id=rid
    ))
  } else {
    concerto.table.query("INSERT INTO `{{table}}` SET `{{item_id_column}}`={{item_id}}, `{{response_column}}`='{{response}}', `{{time_taken_column}}`={{time_taken}}, `{{session_internal_id_column}}`={{session_internal_id}}, `{{correct_column}}`={{correct}}, `{{trait_column}}`='{{trait}}'", list(
      table=table,
      session_internal_id_column=session_internal_id_column,
      session_internal_id=session_internal_id,
      item_id_column=item_id_column,
      item_id=question$id,
      response_column=response_column,
      response=response$response,
      time_taken_column=time_taken_column,
      time_taken=response$timeTaken,
      correct_column=correct_column,
      correct=correct,
      trait_column=trait_column,
      trait=question$trait
    ))
  }

  if(is.null(scores[[question$trait]])) {
    scores[[question$trait]] = 0
  }

  if(!is.null(correctness[current_item]) && !is.na(correctness[current_item])) { 
    prev_correct = correctness[current_item] 
    scores[[question$trait]] = scores[[question$trait]] - prev_correct
  }

  scores[[question$trait]] = scores[[question$trait]] + correct
  return(scores)
}

