    params = inserts
  }
  for(key in ls(template_params)) {
    params[[key]] = template_params[[key]]
  }
  return(params)
}

getNextDebugItem = function(response, questions) {
  for(i in 1:dim(questions)[1]) {
    question = questions[i,]
    if(question$id == response$item_debug) {
      return(i)
    }
  }
  return(NULL)
}

getDebugContent = function(content, question) {
  debug = "<div class='debug'>"
  debug = paste0(debug, "<p>Current item id: #",question$id,"</p>") 
  debug = paste0(debug, "<p>Next item id: <input type='text' name='item_debug' /></p>")
  debug = paste0(debug, "</div>")
  content = paste0(debug, content)
  return(content)
}

#calculate score based on response (e.g. correct/incorrect, or differing number of points depending on choice)
calculateScore = function(response, question) { 
  print("calculateScore()")
  if (is.character(question$response_options)) {
  	o = fromJSON(question$response_options)
  }

  if (o$response_type == "open_text") {
    print("open text item")
    if(response$response == o$correct_answer) { #open text response just needs to match database correct answer
      print("correct")
      return(1)
    } else {
      print("incorrect")
      return(0)
    }
  } else if (o$response_type == "multiple_choice") { #make it go through all the responses, see which one they matched with, and then give them points for the match.
    print("multiple choice item")

    options = NULL #turn response options into a nice table rather than a list.
    for(i in 1:length(o$response_options)) {
      options = rbind(options, as.list(o$response_options[[i]]))
    }

    for(i in 1:dim(options)[1]) {  
      if (options[i,]$value == response$response) { #user chose this response option
        print("response led to this many points:")
        print(options[i,]$points)
        return(as.numeric(options[i,]$points)) #so they get this many points
      }
    }
    print("Warning: User's response did not match any of the options in the item bank. They have been given 0 points for this response.")
    return(0) #no match, so no points
  } 
  return(F)
}

