rb = fromJSON(response_bank)
answers = NULL
response = NULL
warning_msg = NULL
timer_set = FALSE
time_limit = 0
sr_time = fromJSON(stopping_time)
if(!is.null(sr_time$type) && (sr_time$type == "test" || sr_time$type == "item")) { 
  time_limit = as.numeric(sr_time$time_limit) 
}
if(time_limit > 0) { timer_set = TRUE }
scores = list()
correctness = NULL
max_items = dim(questions)[1]
sr_item_count = fromJSON(stopping_item_count)
if(sr_item_count$enabled == "1") { max_items = min(max_items, as.numeric(sr_item_count$count)) }

current_item = 1
while(T) {
  buttons = "<div class='button_panel button_panel_linear'>"
  if(current_item > 1 && can_go_back == "1" && item_debug == "0") {
    buttons = paste0(buttons, "<button name='previous' class='linear_test_button'>",previous_button_label,"</button>")
  }
  if(dim(questions)[1] > 1 && item_debug == "0") {
    buttons = paste0(buttons, current_item,"/",max_items)
  }
  if(current_item <= dim(questions)[1] || item_debug == "1") {
    buttons = paste0(buttons, "<button name='next' class='linear_test_button'>",next_button_label,"</button>")
  }
  buttons = paste0(buttons,'</div>')

  question = questions[current_item,]
  content = getContent(question, current_item, answers)

  if(item_debug == "0") {
    response = concerto.template.show(decoded_template_def$layout, params=getTemplateParams(list(
      header=decoded_template_def$header, 
      title=title,
      content=content, 
      warning_msg=warning_msg, 
      buttons=buttons,
      footer=decoded_template_def$footer
    )), timeLimit=time_limit)
    warning_msg = NULL

    if(timer_set) {
      time_limit = time_limit - as.numeric(response$timeTaken)
    }

    if(!is.null(response$response) || response$isTimeout == "1") {
      correct = 0
      if(is.null(response$response)) { response$response = "" }
      correct = calculateScore(response, question)

      scores = saveResponse(response, question, rb, correct, scores, correctness, current_item)
      if(length(answers) < current_item) {
        answers = c(answers, response$response)
        correctness = c(correctness, correct)
      } else {
        answers[current_item] = response$response
        correctness[current_item] = correct
      }
    }

    prev_item = current_item
    if(response$buttonPressed == "next" || response$isTimeout == "1") {
      if(response_required == "0" || (!is.null(response$response) && response$response != "") || response$isTimeout == "1") {
        current_item = current_item + 1
        if(timer_set && sr_time$type == "item"){
          time_limit = as.numeric(sr_time$time_limit) 
        }
      } else {
        warning_msg = no_response_alert
      }
    }
    if(response$buttonPressed == "previous") {
      current_item = current_item - 1
      if(timer_set && sr_time$type == "item"){
        time_limit = as.numeric(sr_time$time_limit) 
      }
    }

    if(shouldStop(response, answers, questions, prev_item, current_item)) {
      if(response$isTimeout == "1") {
        .branch = "out_of_time"
      }
      break
    }
  } else {
    content = getDebugContent(content, question)
    response = concerto.template.show(decoded_template_def$layout, params=getTemplateParams(list(
      header=decoded_template_def$header, 
      title=title,
      content=content, 
      warning_msg=warning_msg, 
      buttons=buttons,
      footer=decoded_template_def$footer
    )))
    current_item = getNextDebugItem(response, questions)
    if(is.null(current_item)) {
      warning_msg = paste0("Item id ",response$item_debug," not found!")
      current_item = 1
    }
  }
}
