getQuestions = function(){
  questions = NULL
  ib = fromJSON(item_bank)
  if(ib$table_type == "custom") {
    test_id = concerto$mainTest$id
    table = ib$custom_table
    question_column = ib$custom_question_column$column
    order_column = ib$custom_order_column$column
    trait_column = ib$custom_trait_column$column
    reversed_score_column = ib$custom_reversed_score_column$column
    test_id_column = ib$custom_test_id_column$column

    questions = concerto.table.query("SELECT id, `{{question_column}}` AS `question`, `{{trait_column}}` AS `trait`, `{{reversed_score_column}}` AS `reversed_score` FROM `{{table}}` WHERE `{{test_id_column}}` = {{test_id}} ORDER BY `{{order_column}}` ASC", list(
      question_column=question_column,
      order_column=order_column,
      trait_column=trait_column,
      reversed_score_column=reversed_score_column,
      table=table,
      test_id_column=test_id_column,
      test_id=test_id
    ))
  }
  if(ib$table_type == "direct") {
    if(length(ib$direct_table) > 0) {
      for(i in 1:length(ib$direct_table)) {
        questions = rbind(questions, as.list(ib$direct_table[[i]]))
      }
      #put questions in correct order.
      df_questions = as.data.frame(questions)
      df_order = unlist(df_questions$order)
      questions = questions[order(as.numeric(df_order)), ]
    }
  }

  if(dim(questions)[1] > 0 && order == "random") {
    questions = questions[sample(1:dim(questions)[1]),]
  }
  questions = data.frame(questions,stringsAsFactors=F)
  return(questions)
}

getContent = function(questions, current_page, response_set, not_valid, all_responses) {
  content = paragraph
  content = paste0(content, "<table class='questionnaireResponseTable'>")
  content = paste0(content, "<tr>")
  content = paste0(content, "<th class='questionnaireHeaderCell'></th>")
  if(dim(response_set)[1] > 0) {
    for(i in 1:dim(response_set)[1]) {
      response = response_set[i,]
      content = paste0(content, "<th class='questionnaireHeaderCell'>",response$label,"</th>")
    }
  }
  content = paste0(content, "</tr>")
  for(i in ((current_page-1)*as.numeric(items_per_page)+1):(current_page*as.numeric(items_per_page))) {
    if(i > dim(questions)[1]) { break }
    question = questions[i,]
    validationHighlight = ""
    if(question$id %in% not_valid) {
      validationHighlight = "validationHighlight"
    }

    content = paste0(content, "<tr class='",validationHighlight,"'>")
    content = paste0(content, "<td class='questionnaireQuestionCell'>",question$question,"</td>")
    if(dim(response_set)[1] > 0) {
      for(i in 1:dim(response_set)[1]) {
        response = response_set[i,]
        checked = ""
        if(!is.null(all_responses[[paste0("q",question$id)]]) && all_responses[[paste0("q",question$id)]] == response$value) { checked = "checked" }
        content = paste0(content, "<td class='questionnaireResponseCell'><label for='input_response_",question$id,"_",response$value,"' class='questionnaire_label'><input type='radio' id='input_response_",question$id,"_",response$value,"' name='q",question$id,"' value='",response$value,"' ",checked," /></label></td>")
      }
    }
    content = paste0(content, "</tr>")
  }
  content = paste0(content, "</table>")

  return(content)
}

addAnswers = function(answers, response, response_set, questions, rb, all_responses, scores) {
  max_score = max(as.numeric(response_set[,"score"]))
  min_score = min(as.numeric(response_set[,"score"]))

  for(var in ls(response)) {
    if(var == "buttonPressed" || var == "timeTaken" || var == "isTimeout") { next }

    question_id = substr(var, 2, nchar(var))
    value = response[[var]]
    score = NULL
    for(i in 1:dim(questions)[1]) {
      question = questions[i,]
      if(question$id == question_id) {
        trait = toString(question$trait)
        if(is.null(scores[[trait]])) {
          scores[[trait]] = 0
        }
        for(j in 1:dim(response_set)[1]) {
          r = response_set[j,]
          if(r$value == value) {
            score = as.numeric(r$score)
            if(question$reversed_score == "1") {
              score = max_score - score + min_score
            }
            scores[[trait]] = scores[[trait]] + score
            break
          }
        }
        break
      }
    }
    saveResponse(rb, question_id, value, score, trait)
  }
  return(scores)
}

removeAnswers = function(questions, current_page, response_set, all_responses, scores) {
