library(digest)

if(is.null(template_def) || is.na(template_def) || template_def == "") {
  decoded_template_def = fromJSON(concerto.var.get("template_def"))
} else {
  decoded_template_def = fromJSON(template_def)
}

getContent = function() {
  content = paragraph

  if(!is.na(type) && type == "table") {
    content = paste(content, "<div class='formField'><label for='login'>login</label><br/><input type='text' name='login' id='login' /></div>",sep="")
    content = paste(content, "<div class='formField'><label for='password'>password</label><br/><input type='password' name='password' id='password' /></div>",sep="")
  }

  return(content)
}

authorizeUser = function(response) {
  ub = fromJSON(user_bank)

  if(!is.na(type) && type == "table") {
    if(ub$table_type == "direct") {
      users = ub$direct_table
      if(length(users) > 0) {
        for(i in 1:length(users)) {
          user = users[[i]]
          hashed_pass = response$password
          if(user$encryption != "plain") {
            hashed_pass = digest(hashed_pass, user$encryption, serialize=F)
          }
          if(user$login == response$login && user$password == hashed_pass && user$enabled == 1) {
            rm(users)
            return(user)
          }
        }
      }
      rm(users)
    } 
    if(ub$table_type == "default") {
      users = concerto.table.query("SELECT * FROM default_user_table WHERE test_id = {{id}}", list(id=concerto$mainTest$id))
      if(dim(users)[1] > 0) {
        for(i in 1:dim(users)[1]) {
          user = as.list(users[i,])
          hashed_pass = response$password
          if(user$encryption != "plain") {
            hashed_pass = digest(hashed_pass, user$encryption, serialize=F)
          }
          if(user$login == response$login && user$password == hashed_pass && user$enabled == 1) {
            rm(users)
            return(user)
          }
        }
      }
      rm(users)
    } 
    if(ub$table_type == "custom") {
      users = concerto.table.query("SELECT * FROM {{table}} WHERE {{test_column}} = {{id}}", list(table=ub$custom_table, test_column=ub$custom_test_id_column$column, id=concerto$mainTest$id))
      if(dim(users)[1] > 0) {
        for(i in 1:dim(users)[1]) {
          user = list(
            id=users[i, "id"],
            login=users[i, ub$custom_login_column$column],
            password=users[i, ub$custom_password_column$column],
            encryption=users[i, ub$custom_encryption_column$column],
            email=users[i,ub$custom_email_column$column],
            enabled=users[i, ub$custom_enabled_column$column]
          )
          hashed_pass = response$password
          if(user$encryption != "plain") {
            hashed_pass = digest(hashed_pass, user$encryption, serialize=F)
          }
          if(user$login == response$login && user$password == hashed_pass && user$enabled == 1) {
            rm(users)
            return(user)
          }
        }
      }
      rm(users)
    }
  }
  return(NULL)
}

createSession = function(user) {
  sb = fromJSON(session_bank)
  user_id = NULL
  if(!is.null(user)) { user_id = user$id }

  if(sb$table_type == "default") {
    concerto.table.query("INSERT INTO `default_session_table` SET `internal_id` = '{{internal_id}}', `test_id` = {{test_id}}, `user_id` = '{{user_id}}'", list(internal_id=concerto$session$id, test_id=concerto$mainTest$id, user_id=user_id))
  } 
  if(sb$table_type == "custom") {
    concerto.table.query("INSERT INTO `{{table}}` SET `{{iternal_id_column}}` = '{{internal_id}}', `{{test_id_column}}` = {{test_id}}, `{{user_id_column}}` = '{{user_id}}'", list(table=sb$custom_table, iternal_id_column=sb$custom_iternal_id_column$column, internal_id=concerto$session$id, test_id_column=sb$custom_test_id_column$column, test_id=concerto$mainTest$id, user_id_column=sb$custom_user_id_column$column, user_id=user_id, ))
  }
  return(list(id=concerto.table.lastInsertId(), test_id=concerto$mainTest$id, user_id=user_id))
}

doesLoginExists = function(login) {
  ub = fromJSON(user_bank)

  if(!is.na(type) && type == "table") {
    if(ub$table_type == "default") {
      users = concerto.table.query("SELECT * FROM default_user_table WHERE test_id = {{id}} AND login = '{{login}}'", list(
        id=concerto$mainTest$id,
        login=dbEscapeStrings(concerto$connection, toString(login))
      ))
      if(dim(users)[1] > 0) {
        return(TRUE)
      }
    } 
    if(ub$table_type == "custom") {
      users = concerto.table.query("SELECT * FROM {{table}} WHERE {{test_column}} = {{id}} AND {{login_column}} = '{{login}}'", list(
        table=ub$custom_table, 
        test_column=ub$custom_test_id_column$column, 
        id=concerto$mainTest$id,
        login_column=ub$custom_login_column$column, 
        login=dbEscapeStrings(concerto$connection, toString(login))
      ))
      if(dim(users)[1] > 0) {
        return(TRUE)
      }
    }
  }
  return(FALSE)
}

createNewUser = function(login, password, email, enabled) {
  ub = fromJSON(user_bank)
  encryption = "sha1"

  if(!is.na(type) && type == "table") {
    if(ub$table_type == "default") {
      users = concerto.table.query("INSERT INTO default_user_table SET test_id={{id}}, login='{{login}}', password='{{password}}', encryption='{{encryption}}', email='{{email}}', enabled='{{enabled}}'", list(
        id=concerto$mainTest$id,
        login=dbEscapeStrings(concerto$connection, toString(login)),
        password=dbEscapeStrings(concerto$connection, toString(digest(password, encryption, serialize=F))),
        encryption=dbEscapeStrings(concerto$connection, toString(encryption)),
        email=dbEscapeStrings(concerto$connection, toString(email)),
        enabled=dbEscapeStrings(concerto$connection, toString(enabled))
      ))
    } 
    if(ub$table_type == "custom") {
      users = concerto.table.query("INSERT INTO {{table}} SET {{test_column}}={{id}}, {{login_column}}='{{login}}', {{password_column}}='{{password}}', {{encryption_column}}='{{encryption}}', {{email_column}}='{{email}}', {{enabled_column}}='{{enabled}}'", list(
        table=ub$custom_table, 
        test_column=ub$custom_test_id_column$column, 
        id=concerto$mainTest$id,
        login_column=ub$custom_login_column$column, 
        login=dbEscapeStrings(concerto$connection, toString(login)),
        password_column=ub$custom_password_column$column, 
        password=dbEscapeStrings(concerto$connection, toString(digest(password, encryption, serialize=F))),
        encryption_column=ub$custom_encryption_column$column, 
        encryption=dbEscapeStrings(concerto$connection, toString(encryption)),
        email_column=ub$custom_email_column$column, 
        email=dbEscapeStrings(concerto$connection, toString(email)),
        enabled_column=ub$custom_enabled_column$column, 
        enabled=dbEscapeStrings(concerto$connection, toString(enabled))
      ))
    }
    return(concerto.table.lastInsertId())
  }
  return(NULL)
}

sendConfirmationMail = function(uid, email, smtp_host, smtp_port, sender, subject, paragraph, url){
  library(mailR)

  url = concerto.template.insertParams(url, params=list(ecuid=uid))
  recipients = c(email)
  body = paste0(paragraph, "<p><a href='",url,"'>",url,"</a></p>")

  send.mail(
    from = sender,
    to = recipients,
    subject = subject,
    body = body,
    smtp = list(host.name = smtp_host, port = smtp_port),
    html = TRUE, 
    inline = TRUE,
    authenticate = FALSE,
    send = TRUE
  )
}

